require.register('./lib/affiliate-tracking.js', 'lib/', function(global, module, exports, require, __filename, __dirname) {
/*
  BitPay referral tracker
*/
if(!window.bp) window.bp = {};


function setFlashCookie(id) {
  if(!swfStore.get('affiliateId')){
    swfStore.set('affiliateId', id);
  } 
}

function getUrlAid() {
  var results = new RegExp('[\\?&]oid=([^&#]*)').exec(window.location.href);
  if (!results || results.length != 2) {
    return null;
  } else {
    var oid = results[1] || "";
    if(oid.indexOf("_1") != -1) {
      oid = oid.replace("_1", "");
    }
    return oid;
  } 
}

function createCookie(name,value,days) {
  if (days) {
    var date = new Date();
    date.setTime(date.getTime()+(days*24*60*60*1000));
    var expires = "; expires="+date.toGMTString();
  }
  else var expires = "";
  document.cookie = name+"="+value+expires+"; path=/";
}

function readCookie(name) {
  var nameEQ = name + "=";
  var ca = document.cookie.split(';');
  for(var i=0;i < ca.length;i++) {
    var c = ca[i];
    while (c.charAt(0)==' ') c = c.substring(1,c.length);
    if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
  }
  return null;
}

swfStore = new SwfStore({
  namespace: "bitpay",
  swf_url: "https://bitpay.com/downloads/storage.swf",
  onerror: function() {
    console.log("FLASH Failed to load.")
  },
  onready: function() {
    if (window.bp.urlAid) {
      setFlashCookie(urlAid);
    }

    window.bp.flashAid = swfStore.get("affiliateId");
    $('#flashAid').val(window.bp.flashAid);

  }
});

var urlAid = getUrlAid();
if (urlAid) {
  window.bp.urlAid = urlAid;

  if(!readCookie("bp-referral-code")) {
    createCookie("bp-referral-code", urlAid, 9999);
  } 
}
window.bp.cookieAid = readCookie("bp-referral-code");

if(urlAid){
  if(history.replaceState) history.replaceState({}, "", window.location.pathname);
}
$('#cookieAid').val(window.bp.cookieAid)
$('#urlAid').val(window.bp.urlAid);
});

